import{_ as a,c as t,a3 as n,G as r,j as l,B as i,o as s}from"./chunks/framework.C4Vvvhxx.js";const h="/images/idioms/leave-someone-in-the-lurch-1.webp",q=JSON.parse('{"title":"Leave someone in the lurch","description":"","frontmatter":{"title":"Leave someone in the lurch"},"headers":[],"relativePath":"idioms/Idioms to talk about/Behaviour and attitudes/leave-someone-in-the-lurch.md","filePath":"idioms/Idioms to talk about/Behaviour and attitudes/leave-someone-in-the-lurch.md"}'),u={name:"idioms/Idioms to talk about/Behaviour and attitudes/leave-someone-in-the-lurch.md"};function d(m,e,c,p,v,g){const o=i("ExamplesList");return s(),t("div",null,[e[0]||(e[0]=n('<h1 id="leave-someone-in-the-lurch" tabindex="-1">Leave Someone In The Lurch <a class="header-anchor" href="#leave-someone-in-the-lurch" aria-label="Permalink to &quot;Leave Someone In The Lurch&quot;">​</a></h1><img src="'+h+'" class="centered" style="width:100%;max-width:500px;"><p>Фраза <strong>&quot;Leave Someone In The Lurch&quot;</strong> означает оставить кого-то в беде, покинуть человека в сложной ситуации, не предложив помощь. Она используется, чтобы подчеркнуть предательство или безответственное поведение.</p><hr><h2 id="происхождение-фразы" tabindex="-1">Происхождение фразы <a class="header-anchor" href="#происхождение-фразы" aria-label="Permalink to &quot;Происхождение фразы&quot;">​</a></h2><ul><li><strong>Историческое происхождение</strong>: Слово &quot;lurch&quot; изначально обозначало неблагоприятное положение в игре, когда один из игроков оказывался в проигрыше. Со временем это слово стало метафорой для любой сложной или затруднительной ситуации.</li><li><strong>Популяризация</strong>: Фраза впервые была зафиксирована в английском языке в 16 веке и с тех пор получила широкое распространение.</li></ul><hr><h2 id="использование" tabindex="-1">Использование <a class="header-anchor" href="#использование" aria-label="Permalink to &quot;Использование&quot;">​</a></h2><p>Фраза используется для описания:</p><ul><li>Ситуации, когда кто-то бросает другого на произвол судьбы.</li><li>Предательства или безответственного поведения.</li><li>Сложностей, с которыми кто-то сталкивается из-за отсутствия поддержки.</li></ul><hr><h2 id="синонимы" tabindex="-1">Синонимы <a class="header-anchor" href="#синонимы" aria-label="Permalink to &quot;Синонимы&quot;">​</a></h2><ul><li><strong>Abandon someone</strong> — Покинуть кого-то.</li><li><strong>Leave someone high and dry</strong> — Оставить кого-то на произвол судьбы.</li><li><strong>Forsake someone</strong> — Оставить или отказаться от кого-то.</li></ul><hr><h2 id="примеры-использования" tabindex="-1">Примеры использования <a class="header-anchor" href="#примеры-использования" aria-label="Permalink to &quot;Примеры использования&quot;">​</a></h2>',15)),r(o,{"json-path":"/examples/leave-someone-in-the-lurch/leave-someone-in-the-lurch.json"}),e[1]||(e[1]=l("hr",null,null,-1))])}const f=a(u,[["render",d]]);export{q as __pageData,f as default};
